import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { initializeWebSocket } from "./websocket";
import {
  httpLogger,
  logger,
  generalRateLimit,
  createAuthMiddleware,
  errorHandler,
  notFoundHandler
} from "./middleware";
import {
  healthRouter,
  fixturesRouter,
  leaguesRouter,
  standingsRouter,
  teamsRouter,
  predictionsRouter,
  scrapedDataRouter,
  schedulerRouter,
  apiFootballRouter,
  authRouter,
  mlRouter
} from "./routers";
import cookieParser from 'cookie-parser';

// Data initialization utility functions moved to focused routers

export async function registerRoutes(app: Express): Promise<Server> {
  // Trust proxy for rate limiting and IP detection behind load balancers
  app.set('trust proxy', 1);

  // Apply global middleware stack
  logger.info('Applying global middleware stack');
  app.use(cookieParser()); // Parse cookies for session authentication
  app.use(httpLogger); // Structured request logging with request IDs
  app.use(generalRateLimit); // Rate limiting protection (100 req/15min per IP)

  // Mount health endpoints (no auth required) - must be before auth middleware
  app.use('/api', healthRouter);

  // Mount auth endpoints (no auth required for creating sessions)
  app.use('/api/auth', authRouter);

  // Apply required authentication to protected API routes (health and auth endpoints declared before this middleware)
  app.use('/api', createAuthMiddleware({ 
    required: true, 
    skipPaths: ['/health', '/_client-status', '/auth', '/ml/health'] 
  }));

  // Mount focused routers for organized endpoint management
  app.use('/api/fixtures', fixturesRouter);
  app.use('/api/leagues', leaguesRouter); 
  app.use('/api/standings', standingsRouter);
  app.use('/api/teams', teamsRouter);
  app.use('/api/predictions', predictionsRouter);
  app.use('/api/scraped-data', scrapedDataRouter);
  app.use('/api/scheduler', schedulerRouter);
  app.use('/api/football', apiFootballRouter);
  app.use('/api/ml', mlRouter);

  // Initialize with some default leagues
  setTimeout(async () => {
    try {
      console.log('Initializing data...');

      // Try to fetch real data, fallback to sample data if API limits reached
      const leagues = [
        { id: 39, name: 'Premier League', teams: [
          { id: 40, name: 'Liverpool', logo: 'https://media-4.api-sports.io/football/teams/40.png' },
          { id: 50, name: 'Manchester City', logo: 'https://media-4.api-sports.io/football/teams/50.png' },
          { id: 42, name: 'Arsenal', logo: 'https://media-4.api-sports.io/football/teams/42.png' },
          { id: 49, name: 'Chelsea', logo: 'https://media-4.api-sports.io/football/teams/49.png' },
          { id: 33, name: 'Manchester United', logo: 'https://media-4.api-sports.io/football/teams/33.png' }
        ]},
        { id: 140, name: 'La Liga', teams: [
          { id: 541, name: 'Real Madrid', logo: 'https://media-4.api-sports.io/football/teams/541.png' },
          { id: 529, name: 'Barcelona', logo: 'https://media-4.api-sports.io/football/teams/529.png' },
          { id: 530, name: 'AtlÃ©tico Madrid', logo: 'https://media-4.api-sports.io/football/teams/530.png' }
        ]}
      ];

      // Import updateStandings from standings router
      const { standingsRouter } = await import('./routers/standings');

      for (const league of leagues) {
        console.log(`âš ï¸ Using sample data for ${league.name} (ID: ${league.id})`);

        // First, ensure league exists
        await storage.updateLeague({
          id: league.id,
          name: league.name,
          country: league.id === 39 ? 'England' : 'Spain',
          logo: null,
          flag: null,
          season: 2023,
          type: 'League'
        });

        // Store team data FIRST to avoid FK constraint violations
        for (const team of league.teams) {
          await storage.updateTeam({
            id: team.id,
            name: team.name,
            logo: team.logo,
            country: league.id === 39 ? 'England' : 'Spain',
            national: false,
            code: null,
            founded: null
          });
        }

        // Create sample standings AFTER teams exist
        const standingsData = league.teams.map((team, index) => ({
          id: `${league.id}-${team.id}`,
          leagueId: league.id,
          teamId: team.id,
          position: index + 1,
          points: 30 - (index * 3),
          played: 10,
          wins: 8 - index,
          draws: 2,
          losses: index,
          goalsFor: 25 - (index * 2),
          goalsAgainst: 8 + index,
          goalDifference: 17 - (index * 3),
          form: 'WWDWL'
        }));

        await storage.updateStandings(standingsData);
        console.log(`âœ… Sample data seeded for ${league.name}`);
      }

      console.log('Data initialization completed successfully');
    } catch (error) {
      console.error('Error initializing data:', error);
    }
  }, 1000);

  // Apply centralized error handling middleware after all routes
  // Handle 404s for API routes specifically (before SPA fallbacks)
  app.use('/api', notFoundHandler);
  app.use(errorHandler); // Centralized error handling with proper logging and Problem+JSON format

  logger.info('All routes and middleware configured successfully with centralized error handling');

  const httpServer = createServer(app);

  // Initialize WebSocket server for real-time updates
  logger.info('Initializing WebSocket server for real-time updates');
  initializeWebSocket(httpServer);

  return httpServer;
}

server/storage.ts.content:
import {
  type User,
  type InsertUser,
  type League,
  type Team,
  type Fixture,
  type Prediction,
  type Standing,
  type TeamStats,
  type ScrapedData,
  type InsertScrapedData
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Football data methods
  getLeagues(): Promise<League[]>;
  getLeague(id: number): Promise<League | undefined>;
  updateLeague(league: League): Promise<League>;
  updateLeagues(leagues: League[]): Promise<League[]>;

  getTeams(): Promise<Team[]>;
  getTeam(id: number): Promise<Team | undefined>;
  updateTeam(team: Team): Promise<Team>;
  updateTeams(teams: Team[]): Promise<Team[]>;

  getLiveFixtures(): Promise<Fixture[]>;
  getFixtures(leagueId?: number): Promise<Fixture[]>;
  getFixture(id: number): Promise<Fixture | undefined>;
  updateFixture(fixture: Fixture): Promise<Fixture>;
  updateFixtures(fixtures: Fixture[]): Promise<Fixture[]>;

  getPredictions(fixtureId?: number): Promise<Prediction[]>;
  updatePrediction(prediction: Prediction): Promise<Prediction>;

  getStandings(leagueId: number): Promise<Standing[]>;
  updateStandings(standings: Standing[]): Promise<Standing[]>;

  getTeamStats(teamId: number, leagueId?: number): Promise<TeamStats | undefined>;
  updateTeamStats(stats: TeamStats): Promise<TeamStats>;

  // Scraped data methods
  createScrapedData(data: InsertScrapedData): Promise<ScrapedData>;
  getScrapedData(source?: string, dataType?: string, fixtureId?: number, teamId?: number): Promise<ScrapedData[]>;
  getLatestScrapedData(source: string, dataType: string): Promise<ScrapedData | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private leagues: Map<number, League>;
  private teams: Map<number, Team>;
  private fixtures: Map<number, Fixture>;
  private predictions: Map<string, Prediction>;
  private standings: Map<string, Standing>;
  private teamStats: Map<string, TeamStats>;
  private scrapedData: Map<string, ScrapedData>;

  constructor() {
    this.users = new Map();
    this.leagues = new Map();
    this.teams = new Map();
    this.fixtures = new Map();
    this.predictions = new Map();
    this.standings = new Map();
    this.teamStats = new Map();
    this.scrapedData = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getLeagues(): Promise<League[]> {
    return Array.from(this.leagues.values());
  }

  async getLeague(id: number): Promise<League | undefined> {
    return this.leagues.get(id);
  }

  async updateLeague(league: League): Promise<League> {
    this.leagues.set(league.id, league);
    return league;
  }

  async getTeams(): Promise<Team[]> {
    return Array.from(this.teams.values());
  }

  async getTeam(id: number): Promise<Team | undefined> {
    return this.teams.get(id);
  }

  async updateTeam(team: Team): Promise<Team> {
    this.teams.set(team.id, team);
    return team;
  }

  async getLiveFixtures(): Promise<Fixture[]> {
    return Array.from(this.fixtures.values()).filter(
      fixture => fixture.status === 'LIVE' || fixture.status === '1H' || fixture.status === '2H'
    );
  }

  async getFixtures(leagueId?: number): Promise<Fixture[]> {
    const fixtures = Array.from(this.fixtures.values());
    if (leagueId) {
      return fixtures.filter(fixture => fixture.leagueId === leagueId);
    }
    return fixtures;
  }

  async getFixture(id: number): Promise<Fixture | undefined> {
    return this.fixtures.get(id);
  }

  async updateFixture(fixture: Fixture): Promise<Fixture> {
    this.fixtures.set(fixture.id, fixture);
    return fixture;
  }

  async getPredictions(fixtureId?: number): Promise<Prediction[]> {
    const predictions = Array.from(this.predictions.values());
    if (fixtureId) {
      return predictions.filter(pred => pred.fixtureId === fixtureId);
    }
    return predictions;
  }

  async updatePrediction(prediction: Prediction): Promise<Prediction> {
    this.predictions.set(prediction.id, prediction);
    return prediction;
  }

  async getStandings(leagueId: number): Promise<Standing[]> {
    return Array.from(this.standings.values())
      .filter(standing => standing.leagueId === leagueId)
      .sort((a, b) => a.position - b.position);
  }

  async updateStandings(standings: Standing[]): Promise<Standing[]> {
    standings.forEach(standing => {
      this.standings.set(standing.id, standing);
    });
    return standings;
  }

  async updateLeagues(leagues: League[]): Promise<League[]> {
    leagues.forEach(league => {
      this.leagues.set(league.id, league);
    });
    return leagues;
  }

  async updateTeams(teams: Team[]): Promise<Team[]> {
    teams.forEach(team => {
      this.teams.set(team.id, team);
    });
    return teams;
  }

  async updateFixtures(fixtures: Fixture[]): Promise<Fixture[]> {
    fixtures.forEach(fixture => {
      this.fixtures.set(fixture.id, fixture);
    });
    return fixtures;
  }

  async getTeamStats(teamId: number, leagueId?: number): Promise<TeamStats | undefined> {
    return Array.from(this.teamStats.values()).find(
      stats => stats.teamId === teamId && (!leagueId || stats.leagueId === leagueId)
    );
  }

  async updateTeamStats(stats: TeamStats): Promise<TeamStats> {
    this.teamStats.set(stats.id, stats);
    return stats;
  }

  // Scraped data methods for memory storage
  async createScrapedData(data: InsertScrapedData): Promise<ScrapedData> {
    const id = randomUUID();
    const scrapedData: ScrapedData = { 
      ...data, 
      id, 
      createdAt: new Date(),
      fixtureId: data.fixtureId ?? null,
      teamId: data.teamId ?? null,
      confidence: String(data.confidence ?? "0") // Safely convert number to string with fallback
    };
    this.scrapedData.set(id, scrapedData);
    return scrapedData;
  }

  async getScrapedData(source?: string, dataType?: string, fixtureId?: number, teamId?: number): Promise<ScrapedData[]> {
    return Array.from(this.scrapedData.values())
      .filter(data => {
        if (source && data.source !== source) return false;
        if (dataType && data.dataType !== dataType) return false;
        if (fixtureId && data.fixtureId !== fixtureId) return false;
        if (teamId && data.teamId !== teamId) return false;
        return true;
      })
      .sort((a, b) => new Date(b.scrapedAt).getTime() - new Date(a.scrapedAt).getTime())
      .slice(0, 100);
  }

  async getLatestScrapedData(source: string, dataType: string): Promise<ScrapedData | undefined> {
    return Array.from(this.scrapedData.values())
      .filter(data => data.source === source && data.dataType === dataType)
      .sort((a, b) => new Date(b.scrapedAt).getTime() - new Date(a.scrapedAt).getTime())[0];
  }
}

import { DatabaseStorage } from "./db-storage.ts";

// Use database storage when available, memory storage as fallback
const hasDatabase = !!process.env.DATABASE_URL;

export const storage: IStorage = hasDatabase 
  ? new DatabaseStorage() 
  : new MemStorage();

console.log(`Using ${hasDatabase ? 'Database' : 'Memory'} storage`);

