/**
 * Authentication middleware for Bearer token validation
 */

import type { Request, Response, NextFunction } from 'express';
import logger from './logger';
import { AppError } from './errorHandler';
import { auth } from '../config';
import { validateSession } from '../routers/auth';

interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    token: string;
  };
}

/**
 * Authentication middleware - validates Bearer tokens OR session cookies
 */
export function authenticateToken(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  // Try session authentication first (cookie-based)
  if (validateSession(req)) {
    req.user = {
      id: 'session-user',
      token: 'session'
    };

    logger.debug({
      requestId: req.id,
      userId: req.user.id,
      url: req.url,
      authType: 'session'
    }, 'Request authenticated via session');

    return next();
  }

  // Fall back to Bearer token authentication (for API clients)
  const authHeader = req.headers.authorization;
  const token = authHeader && authHeader.split(' ')[1]; // Extract token from 'Bearer TOKEN'

  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    logger.warn({ 
      requestId: req.id,
      ip: req.ip,
      url: req.url 
    }, 'No valid authentication found (session or Bearer token)');

    return next(new AppError(
      'Authentication required. Use session cookie or Bearer token.',
      401,
      'https://tools.ietf.org/html/rfc7235#section-3.1'
    ));
  }

  if (token !== auth.bearerToken) {
    logger.warn({ 
      requestId: req.id,
      ip: req.ip,
      url: req.url
    }, 'Invalid bearer token provided');

    return next(new AppError(
      'Invalid bearer token provided',
      401,
      'https://tools.ietf.org/html/rfc7235#section-3.1'
    ));
  }

  // Attach user info to request for downstream middleware
  req.user = {
    id: 'bearer-user',
    token: token
  };

  logger.debug({
    requestId: req.id,
    userId: req.user.id,
    url: req.url,
    authType: 'bearer'
  }, 'Request authenticated via Bearer token');

  next();
}

/**
 * Optional authentication middleware - validates token if present, allows through if absent
 */
export function optionalAuth(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  const authHeader = req.headers.authorization;

  if (!authHeader) {
    // No auth header - proceed without authentication
    return next();
  }

  // Auth header present - validate it
  return authenticateToken(req, res, next);
}

/**
 * Create context-specific auth middleware
 */
export function createAuthMiddleware(options: {
  required?: boolean;
  skipPaths?: string[];
  customTokenValidator?: (token: string) => boolean;
} = {}) {
  const { required = true, skipPaths = [], customTokenValidator } = options;

  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    // Check if current path should skip auth
    if (skipPaths.some(path => req.path.startsWith(path))) {
      return next();
    }

    if (required) {
      return authenticateToken(req, res, next);
    } else {
      return optionalAuth(req, res, next);
    }
  };
}

export type { AuthenticatedRequest };

