import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { useQuery } from "@tanstack/react-query";
import { TrendingUp, Target, BarChart3, AlertCircle, Activity } from "lucide-react";
import type { ScrapedData } from "@/lib/types";

interface InsightCardProps {
  title: string;
  icon: React.ReactNode;
  data: ScrapedData | null;
  isLoading: boolean;
}

function InsightCard({ title, icon, data, isLoading }: InsightCardProps) {
  if (isLoading) {
    return (
      <Card data-testid={`insight-card-${title.toLowerCase().replace(/\s+/g, '-')}`}>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            {icon}
            <span>{title}</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <Skeleton className="h-6 w-3/4" />
          <Skeleton className="h-4 w-1/2" />
          <Skeleton className="h-8 w-16" />
          <Skeleton className="h-3 w-2/3" />
        </CardContent>
      </Card>
    );
  }

  if (!data) {
    return (
      <Card data-testid={`insight-card-${title.toLowerCase().replace(/\s+/g, '-')}`}>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            {icon}
            <span>{title}</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center space-y-2 py-4">
            <AlertCircle className="h-8 w-8 text-muted-foreground mx-auto" />
            <div className="text-sm text-muted-foreground">No data available</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card data-testid={`insight-card-${title.toLowerCase().replace(/\s+/g, '-')}`}>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            {icon}
            <span>{title}</span>
          </div>
          <Badge variant="secondary" data-testid="confidence-badge">
            {(data.confidence * 100).toFixed(0)}% confidence
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="text-center space-y-2">
          <div className="text-lg font-bold" data-testid="insight-value">
            {data.dataType === 'xg_data' && typeof data.data === 'object' && data.data !== null
              ? `${(data.data as any).expectedGoals || 0} xG`
              : data.dataType === 'team_ratings' && typeof data.data === 'object' && data.data !== null
              ? `${(data.data as any).rating || 0}/100`
              : data.dataType === 'team_form' && typeof data.data === 'object' && data.data !== null
              ? `${(data.data as any).formString || 'N/A'}`
              : 'Processing...'}
          </div>
          <div className="text-sm text-muted-foreground" data-testid="insight-description">
            {data.dataType === 'xg_data' ? 'Expected Goals'
             : data.dataType === 'team_ratings' ? 'Team Performance Rating'
             : data.dataType === 'team_form' ? 'Current Form'
             : data.dataType === 'match_insights' ? 'Match Analysis'
             : data.dataType === 'match_stats' ? 'Match Statistics'
             : 'Advanced Metric'}
          </div>
          <div className="text-xs text-muted-foreground" data-testid="data-source">
            Source: {data.source}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

export function ScrapedInsights() {
  const { data: scrapedData, isLoading, error } = useQuery<ScrapedData[]>({
    queryKey: ['/api/scraped-data'],
    refetchInterval: 30000, // Refresh every 30 seconds for fresh insights
  });

  // Group scraped data by type for display
  const xgData = scrapedData?.find(d => d.dataType === 'xg_data') || null;
  const teamRatings = scrapedData?.find(d => d.dataType === 'team_ratings') || null;
  const teamForm = scrapedData?.find(d => d.dataType === 'team_form') || null;
  const matchInsights = scrapedData?.find(d => d.dataType === 'match_insights') || null;

  if (error) {
    return (
      <div className="space-y-6">
        <Card data-testid="scraped-insights-error">
          <CardContent className="pt-6">
            <div className="text-center space-y-2">
              <AlertCircle className="h-12 w-12 text-destructive mx-auto" />
              <div className="text-lg font-semibold">Unable to load insights</div>
              <div className="text-sm text-muted-foreground">
                Failed to fetch scraped data. Please try again later.
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6" data-testid="scraped-insights">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <InsightCard 
          title="xG Analysis"
          icon={<Target className="h-5 w-5 text-primary" />}
          data={xgData}
          isLoading={isLoading}
        />
        <InsightCard 
          title="Team Ratings"
          icon={<BarChart3 className="h-5 w-5 text-secondary" />}
          data={teamRatings}
          isLoading={isLoading}
        />
        <InsightCard 
          title="Form Analysis"
          icon={<TrendingUp className="h-5 w-5 text-accent" />}
          data={teamForm}
          isLoading={isLoading}
        />
        <InsightCard 
          title="Match Insights"
          icon={<Activity className="h-5 w-5 text-success" />}
          data={matchInsights}
          isLoading={isLoading}
        />
      </div>

      {!isLoading && scrapedData && scrapedData.length === 0 && (
        <Card data-testid="no-scraped-data">
          <CardContent className="pt-6">
            <div className="text-center space-y-4">
              <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto">
                <BarChart3 className="h-8 w-8 text-muted-foreground" />
              </div>
              <div>
                <div className="text-lg font-semibold">Advanced Insights Coming Soon</div>
                <div className="text-sm text-muted-foreground max-w-md mx-auto">
                  Our data scraping services are collecting advanced analytics from multiple sources. 
                  Check back soon for xG data, team ratings, and detailed match insights.
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {!isLoading && scrapedData && scrapedData.length > 0 && (
        <Card data-testid="scraped-data-summary">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5" />
              <span>Data Summary</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-primary">{scrapedData.length}</div>
                <div className="text-xs text-muted-foreground">Total Insights</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-secondary">
                  {new Set(scrapedData.map(d => d.source)).size}
                </div>
                <div className="text-xs text-muted-foreground">Data Sources</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-accent">
                  {((scrapedData.reduce((sum, d) => sum + d.confidence, 0) / scrapedData.length) * 100).toFixed(0)}%
                </div>
                <div className="text-xs text-muted-foreground">Avg Confidence</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-success">
                  {Math.max(...scrapedData.map(d => 
                    Math.floor((new Date().getTime() - new Date(d.scrapedAt).getTime()) / (1000 * 60))
                  ))}m
                </div>
                <div className="text-xs text-muted-foreground">Last Updated</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

server/config/index.ts.content:
/**
 * Centralized configuration management with secure defaults and validation
 */

interface DatabaseConfig {
  url: string;
}

interface ApiConfig {
  footballApiKey: string;
  host: string;
}

interface AuthConfig {
  bearerToken: string;
  scraperToken: string;
}

interface ServerConfig {
  port: number;
  nodeEnv: string;
  logLevel: string;
  version: string;
}

interface AppConfig {
  database: DatabaseConfig;
  api: ApiConfig;
  auth: AuthConfig;
  server: ServerConfig;
}

class ConfigurationError extends Error {
  constructor(message: string, public readonly key: string) {
    super(message);
    this.name = 'ConfigurationError';
  }
}

/**
 * Get required environment variable with validation
 */
function getRequiredEnv(key: string, description?: string): string {
  const value = process.env[key];
  if (!value || value.trim() === '') {
    throw new ConfigurationError(
      `Missing required environment variable: ${key}${description ? ` (${description})` : ''}. ` +
      `Please set this in your environment or Replit Secrets.`,
      key
    );
  }
  return value.trim();
}

/**
 * Get optional environment variable with secure default
 */
function getOptionalEnv(key: string, defaultValue: string): string {
  const value = process.env[key];
  return (value && value.trim() !== '') ? value.trim() : defaultValue;
}

/**
 * Validate API key format and reject insecure defaults
 */
function validateApiKey(key: string, name: string): string {
  if (!key) {
    throw new ConfigurationError(`${name} is required for production use`, name);
  }

  // Reject known insecure defaults
  const insecureDefaults = ['your-api-key', 'dev-key', 'test-key', 'placeholder', 'dummy'];
  if (insecureDefaults.some(insecure => key.toLowerCase().includes(insecure))) {
    throw new ConfigurationError(
      `${name} appears to be a placeholder value. Please set a real API key.`,
      name
    );
  }

  // Basic format validation for API keys
  if (key.length < 10) {
    throw new ConfigurationError(`${name} appears to be too short to be valid`, name);
  }

  return key;
}

/**
 * Validate auth token and reject insecure defaults
 */
function validateAuthToken(token: string, name: string): string {
  if (!token) {
    throw new ConfigurationError(`${name} is required for secure authentication`, name);
  }

  // Reject known insecure defaults
  const insecureDefaults = ['dev-token-12345', 'test-token', 'placeholder', 'dummy-token'];
  if (insecureDefaults.includes(token)) {
    throw new ConfigurationError(
      `${name} is using an insecure default value. Please set a secure token.`,
      name
    );
  }

  // Minimum security requirements
  if (token.length < 20) {
    throw new ConfigurationError(`${name} must be at least 20 characters for security`, name);
  }

  return token;
}

/**
 * Load and validate application configuration
 */
function loadConfig(): AppConfig {
  const nodeEnv = getOptionalEnv('NODE_ENV', 'development');
  const isProduction = nodeEnv === 'production';

  try {
    // Database configuration
    const databaseUrl = getRequiredEnv('DATABASE_URL', 'PostgreSQL connection string');

    // API configuration with multiple fallback env var names
    const apiKeyValue = process.env.API_FOOTBALL_KEY || process.env.RAPIDAPI_KEY;
    if (!apiKeyValue) {
      throw new ConfigurationError(
        'API-Football API key is required. Set either API_FOOTBALL_KEY or RAPIDAPI_KEY environment variable.',
        'API_FOOTBALL_KEY'
      );
    }
    const apiKey = apiKeyValue.trim();

    const validatedApiKey = validateApiKey(apiKey, 'API-Football API key');

    // Authentication configuration with secure validation
    const bearerToken = validateAuthToken(
      getRequiredEnv('API_BEARER_TOKEN', 'API Bearer token for authentication'),
      'API_BEARER_TOKEN'
    );

    const scraperToken = validateAuthToken(
      getRequiredEnv('SCRAPER_AUTH_TOKEN', 'Scraper authentication token'),
      'SCRAPER_AUTH_TOKEN'
    );

    // Server configuration
    const port = parseInt(getOptionalEnv('PORT', '5000'), 10);
    if (isNaN(port) || port < 1 || port > 65535) {
      throw new ConfigurationError('PORT must be a valid port number (1-65535)', 'PORT');
    }

    return {
      database: {
        url: databaseUrl,
      },
      api: {
        footballApiKey: validatedApiKey,
        host: 'v3.football.api-sports.io',
      },
      auth: {
        bearerToken,
        scraperToken,
      },
      server: {
        port,
        nodeEnv,
        logLevel: getOptionalEnv('LOG_LEVEL', 'info'),
        version: getOptionalEnv('npm_package_version', '1.0.0'),
      },
    };
  } catch (error) {
    if (error instanceof ConfigurationError) {
      console.error(`ðŸ”´ Configuration Error: ${error.message}`);

      // In development, provide helpful setup guidance
      if (nodeEnv === 'development') {
        console.error('\nðŸ“‹ Setup Guide:');
        console.error('1. Set required environment variables in .env file or Replit Secrets:');
        console.error('   - DATABASE_URL (provided by Replit PostgreSQL)');
        console.error('   - API_FOOTBALL_KEY (from RapidAPI API-Football subscription)');
        console.error('   - API_BEARER_TOKEN (generate secure random token)');
        console.error('   - SCRAPER_AUTH_TOKEN (generate secure random token)');
        console.error('2. Use Replit Secrets tab for secure credential storage');
        console.error('3. Generate secure tokens: openssl rand -hex 32\n');
      }

      process.exit(1);
    }
    throw error;
  }
}

// Load configuration once at startup
export const config = loadConfig();

// Export configuration sections for easy imports
export const { database, api, auth, server } = config;

// Export validation functions for testing
export { validateApiKey, validateAuthToken, ConfigurationError };

/**
 * Get configuration summary for health endpoints (without sensitive data)
 */
export function getConfigSummary() {
  return {
    environment: server.nodeEnv,
    version: server.version,
    port: server.port,
    logLevel: server.logLevel,
    apiHost: api.host,
    hasDatabase: !!database.url,
    hasApiKey: !!api.footballApiKey && api.footballApiKey.length > 0,
    hasBearerToken: !!auth.bearerToken && auth.bearerToken.length > 0,
    hasScraperToken: !!auth.scraperToken && auth.scraperToken.length > 0,
  };
}

/**
 * Validate all configuration at runtime
 */
export function validateConfiguration(): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  try {
    // Test database URL format
    if (!database.url.startsWith('postgres')) {
      errors.push('DATABASE_URL must be a valid PostgreSQL connection string');
    }

    // Validate API key is not a placeholder
    if (api.footballApiKey.toLowerCase().includes('your-api-key')) {
      errors.push('API_FOOTBALL_KEY appears to be a placeholder value');
    }

    // Validate auth tokens are not defaults
    if (auth.bearerToken === 'dev-token-12345') {
      errors.push('API_BEARER_TOKEN is using insecure default value');
    }

    if (auth.scraperToken.length < 20) {
      errors.push('SCRAPER_AUTH_TOKEN should be at least 20 characters');
    }

    return { valid: errors.length === 0, errors };
  } catch (error) {
    errors.push(error instanceof Error ? error.message : String(error));
    return { valid: false, errors };
  }
}

